fun BannerPager(modifier: Modifier = Modifier, viewModel: HomeViewModel) {
    val pagerState = rememberPagerState()

    /**
     *   
     */
    LaunchedEffect(viewModel){
        viewModel.dispatch(HomeAction.GetBanner)
